import axios from "axios";

const usersURL = 'https://jsonplaceholder.typicode.com/users'


// users utils:
const getAllUsers = async (usersURL) => await axios.get(usersURL)

const getUserByID = async (usersArr, id) =>{
    console.log(usersArr);
    const newarr=usersArr.filter((user)=>user.id===id);
    return newarr[0];
}

const deleteUser = (id,usersArr)=>{
    const userInd = usersArr.findIndex((user)=>user.id===id)
    usersArr.splice(userInd,1)
    return usersArr
}

const updateUser = async (id,obj) => await axios.put(`${usersURL}/${id}`,obj)






export {getAllUsers, getUserByID,updateUser,deleteUser}
