import React, { useState } from 'react'
import { useDispatch } from 'react-redux'

export default function Demo7_AddUser() {
    
    const [user,setUser] = useState({id:0,name:''})

    const dispatch = useDispatch();
    
    return (
        <div>
            Id: <input type="number" onChange={e=>setUser({...user,id:parseInt(e.target.value)})}/><br/>
            Name: <input type="text" onChange={e=>setUser({...user,name:e.target.value})}/><br/>

            <button onClick={()=>dispatch({type:"ADD",payload:user})}>Add</button>
        </div>
    )
}
