import axios from 'axios';

const url = 'http://jsonplaceholder.typicode.com/users'

const getUsers = () =>
{
    return axios.get(url)
}

const getUser = (id) =>
{
    return axios.get(`${url}/${id}`)
}

export {getUsers,getUser}
