import { Component } from "react";
import './Ex3_6TasksComp.css'
class Ex3_6TasksComp extends Component
{
    constructor(props)
    {
        super(props)
    }

    render()
    {
        let taskStyle = this.props.taskData.completed?'completedStyle':'incompletedStyle'
        return (<div style={{border:"4px solid purple", backgroundColor:"lavenderblush"}}>

                Title: {this.props.taskData.title} <br/>
                Completed: <span className={taskStyle}>{this.props.taskData.completed?'true':'false'}</span><br/>
                
        </div>)
    }
}

export default Ex3_6TasksComp;
