import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import Ex9_AllUsers from './Ex9_AllUsers';

export default function Ex9_UsersEditor() {

    const dispatch = useDispatch();

    const [user, setUser] = useState({id:0,fname:'',lname:'',age:0})
    
    return (
        <div>
            Id: <input type="number" onChange={e=>setUser({...user,id:parseInt(e.target.value)})}/><br/>
            First Name: <input type="text" onChange={e=>setUser({...user,fname:e.target.value})}/><br/>
            Last Name: <input type="text" onChange={e=>setUser({...user,lname:e.target.value})}/><br/>
            Age: <input type="number" min="0" max="120" onChange={e=>setUser({...user,age:parseInt(e.target.value)})}/><br/>

            <button onClick={() => dispatch({type:"ADD", payload:user})}>Add</button>
            <button onClick={() => dispatch({type:"UPDATE", payload:user})}>Update</button>
            <button onClick={() => dispatch({type:"DELETE", payload:user.id})}>Delete</button>
            <Ex9_AllUsers/>
        </div>
    )
}
