import React from 'react'
import { useContext } from 'react';
import  { useState } from 'react'
import { useNavigate } from 'react-router';
import { Link } from 'react-router-dom'
import { getPasswordAndUsername } from './DAL_Users'
import './style.css'

//1 page: Login Page (Form with login for users)

export default function LoginPage() {

    const navigate = useNavigate(); // for transiton to another page

    const [username,setUserName] = useState()
    const [userpassword,setUserPassword] = useState()
    //const [[connect,setConnect],[admin,setAdmin]] = useContext([connect,admin])



const goNext = async() =>
    {
        const res = await (await getPasswordAndUsername(username,userpassword))
        console.log(res)
        if (res.letAsses==false){
            alert("Create acount")
        }
        else{
            if(res.admin){
                //setAdmin(true)
                //setConnect(true)
                navigate('/home')
            }
            if(!res.admin){
                setAdmin(false)
                setConnect(true)
                navigate('/home')

            }
        }

    }


    return (
        <div className='loginStyle'>
            <h1>Movies -Subscriptions Web Site</h1>
            <h2>Log in Page</h2>
            <form onSubmit={goNext}>
                    User Name: <input type="text" name="username" onChange={e=>setUserName({...username,username:e.target.value})}/><br/><br/>
                    Password: <input type="text" name="password" onChange={e=>setUserPassword({...userpassword,userpassword:e.target.value})}/><br/>
                    <button type="submit">Login</button><br/>
                    New User ? : <Link to="createAccount">Create Account</Link><br/>
                </form>
        </div>
    )
}
