import React from 'react'
import  { useState } from 'react'
import { useNavigate } from 'react-router';
import { Link, Route,Routes,Outlet } from 'react-router-dom'
import './style.css'

//1 page: Login Page (Form with login for users)

export default function LoginPage() {

    const navigate = useNavigate(); // for transiton to another page

    const [user, setUser] = useState({userName:'',userPassword:''})

    //Navigation to next page (userName - params):
    const goNext = (e) =>
    {
        if (userName==="Admin"||userPassword)
            navigate(`/${user.userName}`)
    }

    return (
        <div className='loginStyle'>
            <h1>Movies -Subscriptions Web Site</h1>
            <h2>Log in Page</h2>
            <form onSubmit={goNext}>
                    User Name: <input type="text" name="username" onChange={e=>setUser({...user,userName:e.target.value})}/><br/><br/>
                    Password: <input type="text" name="password" onChange={e=>setUser({...user,userPassword:e.target.value})}/><br/>
                    <button type="submit">Login</button><br/>
                    New User ? : <Link to="createAccount">Create Account</Link><br/>
                </form>
        </div>
    )
}
