import React, { Component } from "react";

class InputComp extends Component
{
    constructor()
    {
        super()
    }

    handleInputChange = (e) =>
    {
        // console.log(e)
        // console.log(e.target)
        console.log(e.target.value)
        console.log(e.target.name)
    }

    handleCheckboxChange = (e) => 
    {
        console.log(e.target.checked)
        console.log(e.target.type)
        console.log(e.target.name)
    }

    
    render()
    {

        return(<div style={{border:'4px solid brown',backgroundColor:'lightgray'}}>
            <h3>Input Comp</h3>
            Name: <input type="text" name="Name" onChange={this.handleInputChange}/><br/>
            Age: <input type="text" name="age" onChange={this.handleInputChange}/><br/>
            Adult? <input type="checkbox" name="isAdult" onChange={this.handleCheckboxChange}/><br/>
        </div>)

    }
}

export default InputComp
