import axios from "axios";

const usersURL = 'https://jsonplaceholder.typicode.com/users'

const getAllUsers = async () => await axios.get(usersURL)//to get all users---> use to ul of users in Users page

const getUserByID = async (id) => (await axios.get(`${usersURL}/${id}`))//to get one user by each id ---> use in user page

export {getAllUsers, getUserByID}
