//Updating of global states:
function reducer(state = { user:{}, permissions:[], refresh: false , refreshMovies: false}, action) {
    switch (action.type) {
        case "Save User":
            return ({ ...state, user: action.payload })
        case "Save Permissions":
            return ({ ...state, permissions: action.payload })
        case "Refresh User Managment Page":
            return ({ ...state, refresh: !state.refresh })
        case "Refresh movies":
            return ({ ...state, refreshMovies: !state.refreshMovies })
        case "Save ":
        return ({ ...state, editMovie: action.payload })
    }     
}
export default reducer
