import axios from "axios";

const usersURL = 'https://jsonplaceholder.typicode.com/users'
const todosURL = 'https://jsonplaceholder.typicode.com/todos'

// users utils:
const getAllUsers = async (usersURL) => await axios.get(usersURL)

const getUserByID = async (usersURL, id) => await axios.get(`${usersURL}/${id}`)

const updateUser = async (id,obj) => await axios.put(`${usersURL}/${id}`,obj)

// todos utils:


const getAllTodos = async (todosURL) => await axios.get(todosURL)

const getAll = async (url) => await axios.get(url)

const getUserTasks = async (id) => 
{
    // Get the first 5 Titles of the Todos
    // Get all of the user's Todos
    const userTodos = await getAll(`${todosURL}?userId=${id}`)
    
    // Get only the first 5 todos
    const topTodos = userTodos.data.slice(0,1)
    
    return topTodos;
}


export {getAllUsers, getUserByID,updateUser,getAllTodos,getUserTasks}
