import React from 'react'
import  { useState } from 'react'
import { useNavigate } from 'react-router';

//1 page:

export default function LoginPage() {

    const navigate = useNavigate();// for transiton to another page


    const [user, setUser] = useState({userName:'',userPassword:''})
    // Determine stste--->user object(isValid for first name mangetory, in state its false , because state of first name is empty)

    //goNext - function for button(if fname is empty--->alert masage, else going to city page)
    const goNext = () =>
    {
            navigate(`/city/${user.userName}`)//  /city-to next page and(user.fname,user.lname-params)
    }

    return (
        <div className='loginStyle'>
            <h2>Log in Page</h2>
            <form onSubmit={goNext}>
                    User Name: <input type="text" name="username" onChange={e=>setUser({...user,userName:e.target.value})}/><br/>
                    Password: <input type="text" name="password" onChange={e=>setUser({...user,userPassword:e.target.value})}/><br/>
                    <button type="submit">Login</button>
                </form>
        </div>
    )
}
