import { Component } from "react";

class Demo10_CB_Func2_Child extends Component
{
    constructor(props)
    {
        super(props)
        this.state={greetings: ''}
    }

    inputHandler = (e) =>
    {
        this.setState({greetings:e.target.value})
    }

    sendDataToParent = () =>
    {
        this.props.callback(this.state.greetings)
    }

    render()
    {
        return (<div style={{border:"2px solid orange", backgroundColor:"lightgoldenrodyellow", width:"300px"}}>

        <h3>Demo10 CB-Func2_Child</h3>
        Data from Parent: {this.props.dataToChild} <br/>
        Send to Parent: <input type="text" onChange={this.inputHandler}/>

        <button onClick={this.sendDataToParent}>Send Data</button>

</div>)
    }
}

export default Demo10_CB_Func2_Child;
