import { Component } from "react";
import Demo8ProductComp from "./Demo8_Product";

class Demo8_ProductsComp extends Component
{
    constructor()
    {
        super()
        this.state = {
            products:[{name:"pc",price:100},
                        {name:"Watch",price:120},
                        {name:"TV",price:200}],
                        name:"",price:0
        }
        
    }

    addProd = ()=>
    {
        let obj ={name:this.state.name, price:this.state.price}

        this.setState({products:[...this.state.products,obj]})
    }

    render()
    {    
        const products = this.state.products.map((product,index) => 
        {
            return <div><Demo8ProductComp product={product} key={index}/><br/></div>
        })

        return (<div style={{border:"4px solid blue", backgroundColor:"ivory"}}>
                    <h3>Demo 8</h3>

                    <h4>Add New Product</h4>
                    Name: <input type="text" onChange={(e)=>this.setState({name:e.target.value})}/><br/>
                    Price: <input type="number" onChange={(e)=>this.setState({price:e.target.value})}/><br/>
                    <input type="button" value="Add" onClick={this.addProd}/>

                    <h2>Products List</h2>
                    {products}

                    
                
                </div>)
    }

}
export default Demo8_ProductsComp;
