import axios from 'axios'
import React, { useEffect } from 'react'
import { useDispatch } from 'react-redux';
import CounterViewer from './CounterViewer';
import Demo7_AddUser from './Demo7_AddUser';
import Demo7_Users from './Demo7_Users';
//1. begining from this:
export default function Demo7_Container() {
    
    const dispatch = useDispatch()
    
    useEffect(async () => 
    {
        const resp = await axios.get("http://jsonplaceholder.typicode.com/users");
        dispatch({type:"LOAD", payload:resp.data})
    },[])
    
    return (
        <div>
            <Demo7_Users/>
            <Demo7_AddUser/>
            <CounterViewer/>
        </div>
    )
}
