import React from 'react'
import { useNavigate ,useParams} from 'react-router';
import { useState } from 'react';

//3.
export default function CityPage() {
    const params = useParams()

    const [user,setUser] = useState({city:""})

    const navigate = useNavigate();// for transiton to another page

    const next =()=>
    {
        navigate(`/data/${params.fname}/${params.lname}/${user.city}`)
    }


    return (
        <div style={{background:"yellow", width:"400px", height:"400px"}}>
            <h2>Choose Your City:</h2>
            City: <select onChange={(e)=>setUser({...user,city:e.target.value})}>
                    <option value=''></option>
                    <option value='Tel Aviv'>Tel Aviv</option>
                    <option value='Jerusalem'>Jerusalem</option>
                    <option value='Haifa'>Haifa</option>
                </select>
            <button onClick={next}>Next</button>

        </div>
    )
}
