import { Component } from "react";
import { getUserByID } from "../DALs/DAL_USERS";
import { getUserTasks, uncompletedTask } from "../DALs/DAL_TODOS";
import { getUserPosts } from "../DALs/DAL_POSTS";
import Tasks from "./Tasks";
import Posts from "./Posts";
import "./styleDiv.css"



const usersURL = 'https://jsonplaceholder.typicode.com/users'

export default class Users extends Component {
    constructor(props) {
        super(props)
        this.state = { id: '', name: "", email: "", user: {}, tasks: [], posts: [], address: [], completed: "", isShowingData: false, mouseInside: false, isVisible: false }
    }



    async componentDidMount() {
        const user = {}
        // const user = await getUserByID(usersArr, this.props.id)

        const taskStatus = await uncompletedTask(this.props.id)
        if (taskStatus === true) {
            this.setState({ completed: true })
        }
        if (taskStatus === false) {

            this.setState({ completed: false })
        }

    }
    componentDidUpdate() {
        console.log('from users: ', this.props.user);
        if ((this.state.id !== this.props.id) || this.props.refresh) {
            const user = this.props.user
            this.setState({ user: user, id: this.props.id })
            this.props.refreshStopper()
            this.setState({ name: user.name, email: user.email, address: user.address })
        }
        if (this.state.id === this.props.pressedId) {
            this.setState({ orange: true })
        }
        else{
            this.setState({ orange: false })

        }

    }



    showDataWindow = async () => {


        if (!this.state.isShowingData) {
            const tasks = await getUserTasks(this.props.id)
            const posts = await getUserPosts(this.props.id)
            this.setState(prevState => ({ tasks, isShowingData: !prevState.isShowingData }))
            this.setState({ posts: posts })
            this.setState({ isVisible: true })
            this.props.pressed(this.state.id)
            //this.setState(prevState =>({isVisible:!prevState.isVisible}))

        }
        else {
            this.setState(prevState => ({ tasks: [], isShowingData: !prevState.isShowingData }))
            this.setState({ isVisible: false })

        }
        console.log(this.state.isVisible)
    }


    mouseEnter = () => {
        this.setState({ mouseInside: true });
    }

    mouseLeave = () => {
        this.setState({ mouseInside: true });
    }

    mouseClick = () => {
        this.setState({ mouseInside: false });
    }


    render() {
        let colorStyle;
        let colorBack;

        colorStyle = this.state.completed ? 'greenStyle' : 'redStyle'
        colorBack = this.state.isVisible ? 'orangeStyle' : 'whiteStyle'

        const tasksRep = this.state.tasks.map((task, index) => {
            return (<div key={index}><Tasks todo={task} /><br /></div>)
        })

        const postsRep = this.state.posts.map((post, index) => {
            return (<div key={index}><Posts post={post} /></div>)
        })


        const adressUser = <div style={{ border: '2px solid violet', borderRadius: '12px', padding: '5px', backgroundColor: 'pink' }} onClick={this.mouseClick}>
            Street: <input type='text' value={this.state.address.street} /><br />
            City: <input type='text' value={this.state.address.city} /><br />
            Zip Code: <input type='text' value={this.state.address.zipcode} /><br />
        </div>


        const showingTaskWindow = <div>{this.state.isShowingData ? <div style={{
            border: '2px solid black', background: "white", borderRadius: '12px', padding: '5px', width: '300px', height: '300px', overflow: 'scroll', margin: 'left', position: 'absolute', top: '50px', textAlign: 'left', left: '700px'
        }}> Todos - User {this.props.id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button style={{ background: 'yellow' }}>ADD</button><br /><br />
            {tasksRep}</div> : ''}</div>

        const showingPostWindow = <div>{this.state.isShowingData ? <div style={{
            border: '2px solid black', background: "white", borderRadius: '12px', padding: '5px', width: '300px', height: '300px', overflow: 'scroll', margin: 'left', position: 'absolute', top: '400px', textAlign: 'left', left: '700px'
        }}> Posts - User {this.props.id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button style={{ background: 'yellow' }}>ADD</button><br /><br />
            {postsRep}</div> : ''}</div>





        return (
            <div> {showingTaskWindow}
                {showingPostWindow}
                <div style={{}} className={colorBack}><div className={colorStyle}>
                    <div onClick={this.showDataWindow}><h4>ID: {this.props.id}</h4></div>

                    Name:<input type="text" value={this.state.user.name} /><br />
                    Email:<input type="email" value={this.state.user.email} /><br />
                    <div>
                        {this.state.mouseInside ? adressUser : null}
                    </div>
                    <button onMouseEnter={this.mouseEnter} onMouseLeave={this.mouseLeave}>Other Data</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <button>Update</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <button>Delete</button><br />

                </div></div>
            </div>)
    }
}
