import { Component } from "react";
import { getUserTasks, uncompletedTask } from "../DALs/DAL_TODOS";
import { getUserPosts } from "../DALs/DAL_POSTS";
import Tasks from "./Tasks";
import Posts from "./Posts";
import "./styleDiv.css"

const usersURL = 'https://jsonplaceholder.typicode.com/users'

export default class Users extends Component {
    constructor(props) {
        super(props)
        this.state = { 
            id:'', 
            name:'', 
            email:'', 
            user:{}, 
            tasks:[], 
            posts:[], 
            address:[], 
            completed:'', 
            isShowingData: false, 
            mouseInside: false, 
            isVisible: false, 
            changeInput:false 
        }
    }
   
    async componentDidMount() {
        const taskStatus = await uncompletedTask(this.props.id) //The component is loading the data of the todos when it mounts.
        //Checking if task complited or not to changing a color of div border:
        if (taskStatus === true) 
        {
            this.setState({ completed: true })
        }
        if (taskStatus === false) 
        {
            this.setState({ completed: false })
        }
    }
    
    componentDidUpdate() {
        if ((this.state.id !== this.props.id) || this.props.refresh) //Checking if all the users have been updated after search
        {
            const user = this.props.user
            this.setState({ user: user, id: this.props.id })
            this.props.refreshStopper()
            this.setState({ name: user.name, email: user.email, address: user.address })
        }
    }

    //Function for 2 windows in which todos and posts of each user are transferred:
    showDataWindow = async () => {
        if (!this.state.isShowingData) 
        {
            const tasks = await getUserTasks(this.props.id)
            const posts = await getUserPosts(this.props.id)
            this.setState(prevState => ({ tasks, isShowingData: !prevState.isShowingData }))
            this.setState({ posts: posts })
            this.setState({ isVisible: true })
            this.props.pressed(this.state.id)
        }
        else 
        {
            this.setState(prevState => ({ tasks: [], isShowingData: !prevState.isShowingData }))
            this.setState({ isVisible: false })
        }
    }
    //Function that assigns a boolean value true to a variable when mouse is inside a div:
    mouseEnter = () => {
        this.setState({ mouseInside: true });
    }

    //Function that assigns a boolean value false to a variable on click a button:
    mouseClick = () => {
        this.setState({ mouseInside: false });
    }

    //Delete user:
    handleDeleteUser = () => this.props.deleteFunction(this.props.user.id)


    render() {
        let colorStyle;
        let colorBack;

        colorStyle = this.state.completed ? 'greenStyle' : 'redStyle'
        colorBack = this.state.id===this.props.pressedId ? 'orangeStyle' : 'whiteStyle'

        const tasksRep = this.state.tasks.map((task, index) => {
            return (<div key={index}><Tasks todo={task} /><br /></div>)
        })

        const postsRep = this.state.posts.map((post, index) => {
            return (<div key={index}><Posts post={post} /></div>)
        })

        const adressUser = <div className='adressStyle' >
            Street: <input type='text' value={this.state.address.street} /><br />
            City: <input type='text' value={this.state.address.city} /><br />
            Zip Code: <input type='text' value={this.state.address.zipcode} /><br />
        
        </div>
        const showingTaskWindow = <div>{this.state.isShowingData ? <div className='tasksPostsStyle'> 
        Todos - User {this.props.id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <button style={{ background: 'yellow' }} onClick={this.showAddTodo}>ADD</button><br /><br />
        {tasksRep}</div> : ''}
        </div>

        const showingPostWindow = <div>{this.state.isShowingData ? <div className='tasksPostsStyle' style={{top: '400px'}}> 
        Posts - User {this.props.id}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <button style={{ background: 'yellow' }}>ADD</button><br /><br />
        {postsRep}</div> : ''}</div>

        return (
            <div> 
                {showingTaskWindow}
                {showingPostWindow}
                <div className={colorBack}><div className={colorStyle}>
                <div onClick={this.showDataWindow}><h4>ID: {this.props.id}</h4></div>
                Name:<input type="text" name= "name" value={this.state.name} /><br />
                Email:<input type="email" value={this.state.user.email} /><br />
                <div>
                    {this.state.mouseInside ? adressUser : null}
                </div>
                <button onClick={this.mouseClick} onMouseEnter={this.mouseEnter} >Other Data</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <button onClick = {this.handleDeleteUser}>Delete</button><br />

                </div></div>
            </div>)
    }
}
