import { Component } from "react";


export default class Tasks extends Component
{
    constructor(props)
    {
        super(props)
        this.state={completed:"",title:""}
    }

    async componentDidMount()
    {

        this.setState({completed:todo.data.completed,title:todo.data.title})

    }


    render()
    { 
        return (<div style={{border:'2px solid black',width:'100px', height:'100px'}}>
            Title: < input value={this.props.todo.title}/> <br/>
            Completed: <input  value={this.props.todo.completed}/>
        </div>)
    }
}
