import React, { Component } from 'react';
import { getAllUsers } from '../DALs/DAL_USERS';
import Users from './Users';

const usersURL = 'https://jsonplaceholder.typicode.com/users'

class Main extends Component {
    constructor()
    {
        super()
        this.state = {users:[], filteredUsers:[], names:[],emails:[]}
    }
    async componentDidMount()
    {
        const users = await getAllUsers(usersURL)
        this.setState({users:users.data})
        this.setState({filteredUsers:users.data})
        this.setState({names:users.data.name})
        this.setState({emails:users.data.email})
    }

    handleInputChange = (e) => {
        let searchedText = e.target.value;
        console.log("Search text is: " +searchedText)
            
            if (searchedText.length===0)
            {
                 this.setState({filteredUsers:this.state.users})
                 console.log("test")
            }
            else
            {
                this.setState({filteredUsers:this.state.users})
                
                console.log("Filtered users --->before filter:")
                console.log(this.state.filteredUsers)
                
                const searchedUsers = this.state.users.filter((user)=>user.name.includes(searchedText)||user.email.includes(searchedText))
                
                console.log("filter:")
                console.log(searchedUsers)
                
                this.setState({filteredUsers:[...searchedUsers]})
               
                
                console.log("Filtered users --->after filter:")
                console.log(this.state.filteredUsers)

            }
            
        searchedText = ""
    }


    render() {
       
            const repUsers = this.state.filteredUsers.map((user,index) => <div key={index}><Users  key ={index} id={user.id}/><br/></div>)
            return(
                <div style={{ border: '2px solid black',background:'pink', borderRadius: '12px', padding: '5px', width: '500px', textAlign: 'center', margin:'auto'}}>

                Search <input type="text"  onChange={this.handleInputChange}/>&nbsp;&nbsp;
                <button>Add</button><br/><br/>
                {repUsers} 

            </div>
        );
    }
}
export default Main
