import axios from "axios";

const usersURL = 'https://jsonplaceholder.typicode.com/users'

//Getting all users:
const getAllUsers = async (usersURL) => await axios.get(usersURL)

//Getting user by ID of user:
const getUserByID = async (usersArr, id) =>{
    //console.log(usersArr);
    const newarr=usersArr.filter((user)=>user.id===id);
    return newarr[0];
}
//Delete user from array of users:
const deleteUser = (id,usersArr)=>{
    const userIndex = usersArr.findIndex((user)=>user.id===id)
    usersArr.splice(userIndex,1)
    return usersArr
}



export {getAllUsers, getUserByID,deleteUser}
