import { Component } from "react";

class Demo6_Repeater extends Component
{
    constructor()
    {
        super()
        this.state = {colors: ['blue', 'purple', 'orange']}
    }

    addColor = () =>
    {
        //this.state.colors.push('yellow') -> WRONG WAY

        let newColor = 'yellow'

    //    const colorsArr = [...this.state.colors, newColor]
        //colorsArr.push(newColor)

  //      this.setState({colors:colorsArr})

        // Option2
        this.setState({colors:[...this.state.colors, newColor]})
    }

    render()
    {
        return (<div style={{border:"4px solid hotpink", backgroundColor:"lavender"}}>
            <h3>Demo6 - Repeater</h3>
            <button onClick={this.addColor}>Add Color</button>
            <ul>
                {this.state.colors.map((color, index) => {
                    return <li key={index}>{color}</li>
                })}
            </ul>
        </div>)
    }

}

export default Demo6_Repeater;
