import React from 'react'
import  { useEffect, useState } from 'react'
import { useNavigate } from 'react-router';


export default function FormPage() {

    const navigate = useNavigate();


    const [user, setUser] = useState({fname:'',lname:'',isValid:false})

    const goNext = (e) =>
    {
        e.preventDefault();
        if (user.isValid)
        {
            navigate(`/city/${user.fname}/${user.lname}`)
        }
        else
        {
            alert('First name is mandatory')
        }  
    }

    const handleChange =(e)=>
    {
        setUser({...user,fname:e.target.fname,lname:e.target.lname})
    }



    return (
        <div style={{background:"yellow", width:"400px", height:"400px"}}>
            <h2>Form</h2>
            <form onSubmit={goNext}>
                    First Name: <input type="text" name="fname" onChange={(e)=> {handleChange(e) ,handleError(e) }}/><br/>
                    Last Name: <input type="text" name="lname" onChange={handleChange}/><br/>
                    <button type="submit">Next</button>
                </form>

        </div>
    )
}
