import { Component } from "react";

class Ex2_4RadioAndCheckboxComp extends Component
{
    constructor()
    {
        super()
        this.state={isAdult:false}
    }

    setAdult = (e) =>
    {
        if (e.target.value == "Male")
        {
            this.setState({isAdult:false})
        }
        else
        {
            this.setState({isAdult:true})
        }
    }

    // checkboxHandler = (e) =>
    // {
    //     let isAdult = e.target.checked;
    //     this.setState({isAdult})
    // }

    render()
    {
        return(<div>
            Male:  <input type="radio" name="gender" value="Male" onChange={this.setAdult}/><br/>
            Female:<input type="radio" name="gender" value="Female" onChange={this.setAdult}/><br/>
            isAdult: <input type="checkbox" checked={this.state.isAdult}/>
        </div>)
    }
}
export default Ex2_4RadioAndCheckboxComp;
