import { Component } from "react";

class Ex3_3_Repeater_TableComp extends Component
{
    constructor()
    {
        super()
        this.state = {
            persons:[
                {name:"Dana", age:20,city:"Haifa"},
                {name:"Ron", age:22,city:"Tel Aviv"},
                {name:"Dov", age:31,city:"Ashdod"},
                {name:"Vered", age:19,city:"Eilat"}
            ], 
            name:'',age:'',city:''
        }
    }

    //Option 1
    // nameHandler = (e) =>
    // {
    //     this.setState({name:e.target.value})
    // }
    // ageHandler = (e) =>
    // {
    //     this.setState({age:e.target.value})
    // }
    // cityHandler = (e) =>
    // {
    //     this.setState({city:e.target.value})
    // }

    handleChange = (e) =>
    {
        // Option 1
        // const name = e.target.name;
        // const value = e.target.value;
        const {name, value} = e.target
        
        this.setState({[name]:value})
    }

    addPerson = () =>
    {
        const newPerson =   {name:this.state.name,
                            age:this.state.age,
                            city:this.state.city}
        
        // Option 1
        // const persons = [...this.state.persons]
        // persons.push(newPerson)

        // Option 2
        const persons = [...this.state.persons, newPerson]
        
        this.setState ({persons})

        // Option 3
        //this.setState ({persons: [...this.state.persons, newPerson]})
    }

    render()
    {

        const rows = this.state.persons.map((person,index)=>
        {
            return <tr key={index}>
                        <td>{person.name}</td>
                        <td>{person.age}</td>
                        <td>{person.city}</td>
                    </tr>
        })

        return (<div>
            <h3>Ex3_3 + Ex3_5</h3>
            
            {/* Ex3_5 */}
            Name:<input type="text" name="name" onChange={this.handleChange}/><br/>
            Age:<input type="text" name="age"onChange={this.handleChange}/><br/>
            City:<input type="text" name="city"onChange={this.handleChange}/><br/>
            <button onClick={this.addPerson}>Add</button><br/>
            <br/>
            <br/>

            <table border='1'>
                <thead>
                    <tr>                    
                        <th>Name</th>
                        <th>Age</th>
                        <th>City</th>    
                    </tr>
                </thead>
                <tbody>
                    {rows}
                </tbody>
            </table>


        </div>)
    }
}
export default Ex3_3_Repeater_TableComp
