/*
action - a json with:
- type (mandatory) - the type of the action
- payload (optional) - the data send with the action

*/
function reducer(state = {users:[], counter:0}, action)
{
    switch(action.type)
    {
        case "LOAD":
            return {...state, users: action.payload,counter:action.payload.length}
        
        case "ADD":
            return {...state, users: [...state.users,action.payload], counter: state.counter + 1}
        
        default:
            return state;
    }
}

export default reducer;
