import React from 'react'
import { useSelector } from 'react-redux'
import { Outlet, useNavigate } from 'react-router-dom'

export default function MoviesComp() {
    const navigate = useNavigate()
    const permissions = useSelector(state => state.permissions)

    //Link the client to the addmovie page only if the user has the permission to do it:
    const addMovie = () =>
    {
        let boolean = false
        for (let permission of permissions)//for each permission
            if (permission === "Create Movies"){
            //checking if user has permission 
                boolean = true
            }
        boolean ? navigate('/mainPage/movies/allMovies') : alert("You Don't Have The Permission To View Movies")

    }

    return (
        <div style={{margin:'auto',textAlign:'center'}}>
        <h2>Movies</h2>
            <button className='seconderyBtns' onClick={() => navigate('/mainPage/movies/allMovies')}>All Movie</button>
            <button className='seconderyBtns' onClick={addMovie}>Add Movie</button>
            <Outlet />
        </div>
    )
}
