import { Component } from "react";

class Ex2_3SwapComp2 extends Component
{
    constructor()
    {
        super()
        this.state = {input1:"", input2:"", person:{name:"Avi", age:30}}
    }

    swapData = () =>
    {
        let currentName = this.state.person.name;
        let currentAge = this.state.person.age;
        this.setState({person:{name:currentAge,age:currentName}});
        if ((this.state.input1.length > 4) || (this.state.input2.length > 4))
        {
            this.setState((prevState)=> ({input1:prevState.input2,input2:prevState.input1}))
        }
    }

    // Option 1
    handlerinput1 = (e) =>
    {
        this.setState({input1:e.target.value})
    }

    handlerinput2 = (e) =>
    {
        this.setState({input2:e.target.value})
    }

    render()
    {
        return(<div>
            <span>Name: {this.state.person.name}</span><br/>
            <span>Age: {this.state.person.age}</span><br/>
            <input type="text" value = {this.state.input1} onChange={(e)=>this.setState({input1:e.target.value})}/><br/>
            <input type="text" value = {this.state.input2} onChange={(e)=>this.setState({input2:e.target.value})}/><br/>
            <button onClick={this.swapData}>Swap</button>
        </div>)
    }

}

export default Ex2_3SwapComp2;
