/*
action - a json with:
- type (mandatory) - the type of the action
- payload (optional) - the data send with the action

*/
function reducer(state = {counter:0}, action)
{
    switch(action.type)
    {
        case "INCREMENT":
            return {...state, counter: state.counter + action.payload}
        
        case "DECREMENT":
            return {...state, counter: state.counter - action.payload}
        
        default:
            return state;
    }
}

export default reducer;
