import { Component } from "react";

class Ex3_2_ColorsCheckboxComp extends Component
{
    constructor()
    {
        super()
        this.state = {red:false, green:false, blue:false}
    }

    greenHandler = ()=>
    {
        this.setState((prevState) => ({green:!prevState.green}))
    }

    render()
    {
        const redItem = this.state.red? <li>Red</li>:null
        const greenItem = this.state.green?<li>Green</li>:null
        const blueItem = this.state.blue?<li>Blue</li>:null

        return (<div>

                <h3>Ex3_2</h3>
                <input type="checkbox" onChange={()=>(this.setState((prevState)=> ({red:!prevState.red})))}/> Red <br/>
                <input type="checkbox" onChange={this.greenHandler}/> Green <br/>
                <input type="checkbox"  onChange={()=>(this.setState((prevState)=> ({blue:!prevState.blue})))}/> Blue<br/>
                <ul>
                    {redItem}
                    {greenItem}
                    {blueItem}
                </ul>

        </div>)
    }

}

export default Ex3_2_ColorsCheckboxComp
