import axios from "axios";

const usersURL = 'http://localhost:9000'

//Sending Password and Username:
const getPasswordAndUsername = async (username, password) =>
{
    const response = await axios.post(usersURL,{username:username,password:password})
    return response 
}

export {getPasswordAndUsername}
