import React,{useState,useEffect} from "react"
import { getPermissionsArray, moviesPermissionsPressed, subscriptionsPermissionsPressed } from "../Utils/Users_Utils"
export default function OneEditUser(props)
{
    //states:
    const [ user,setUser ] = useState()
    const [ userJson,setUserJSON ] = useState({})
    const [ firstName,setFirstName ] = useState('')
    const [ lastName,setLastName ]  = useState('')
    const [ username,setUsername ] = useState('')
    const [ sessionTimeOut,setSessionTimeOut ] = useState(0)
    const [ permissions,setPermissions ] = useState({})
    const [ permissiosBooleanList,setPermissionsBooleanList ] = useState([false,false,false,false,false,false,false,false])
    const [ permissionsStringsList,setPermissionsStringsList ] = useState(["View Subscriptions","Create Subscriptions","Delete Subscriptions","Update Subscriptions","View Movies","Create Movies","Delete Movies","Update Movie"])
    const [PermissionsBOOL, setPermissionsBOOL] = useState([])
    const [ refreshPermissions, setRefreshPermissions] = useState()

   //Loading all data in the inputs:
    useEffect(()=> {
        const editUser = props.user;
        setUser(editUser.user);
        setUserJSON(editUser.userJson);
        setPermissions(editUser.permissions.permissions);
        const userArr = getPermissionsArray(editUser.permissions.permissions)
        setPermissionsBooleanList(userArr)
        setPermissionsBOOL(userArr)
    },[])

    //Checking constantly the permission to "check" the "view" permissoin accordingly
    useEffect(() => {
        const arr = [...PermissionsBOOL]
        if (subscriptionsPermissionsPressed(PermissionsBOOL)) {
            arr[0] = true
            setPermissionsBOOL(arr)
            setPermissionsBooleanList(arr)
        }
        if (moviesPermissionsPressed(PermissionsBOOL)) {
            arr[4] = true
            setPermissionsBOOL(arr)
            setPermissionsBooleanList(arr)
        }
    }, [refreshPermissions])

    //Saving the client's inputs of permissions: 
    const changePermission = (e) => {
        const arr = [...PermissionsBooleanList]
        e.target.checked ? arr[e.target.name] = true : arr[e.target.name] = false
        setPermissionsBooleanList(arr)
        setPermissionsBOOL(arr)
        setrefresh(!refresh)
    }


    let permissionsList = ''
    console.log(PermissionsBOOL)
    if (PermissionsBOOL[0] !== undefined)
    {
        permissionsList = permissionsStringsList.map((per,index) =><span ket={index}><input type = 'checkbox' name ={index} onChange={changePermission}/></span>)
    }

    return(
        <div className="oneEditUserDiv">
            First Name : <input type ='text' defaultValue={userJson.firstName} onChange={e=>setFirstName(e.target.value)}/><br/>
            Last Name : <input type ='text' defaultValue={userJson.lastName} onChange={e=>setLastName(e.target.value)}/><br/>
            Username : <input type ='text' defaultValue={user?.username} onChange={e=>setUsername(e.target.value)}/><br/>
            Session time out (Minutes) : <input type ='number'defaultValue={userJson.sessionTimeOut} onChange={e=>setSessionTimeOut(e.target.value)}/><br/>
            Created data : <input value={userJson.createdDate}/><br/>
            Permissions:<br/>
            {permissionsList}


        </div>
    )
}
