import React from 'react'
import { useSelector } from 'react-redux'

export default function Ex9_AdultUsers() {
    
    const storeData = useSelector(state => state)

    const usersRep = storeData.users.filter(user=>user.age>=18)
                                .map((user,index) =>
    {
        return(<tr key={index}>
            <td>{user.id}</td>
            <td>{user.fname}</td>
            <td>{user.lname}</td>
            <td>{user.age}</td>
        </tr>)
    } )

    return (
        <div>
            <h3>Adult Users</h3>
            <table border="1">
                {usersRep}
            </table>
        </div>
    )
}
