import React, { Component } from 'react';
import { Switch,Route,Link } from 'react-router-dom';


class PersonPage extends Component 
{
    constructor()
    {
        super()
        this.state={firstName:"",lastName:""}
    }

    handleFirstName =(e)=>
    {
        this.setState({firstName:e.target.value})
    }
    handleLasttName =(e)=>
    {
        this.setState({lastName:e.target.value})
    }


    render() {
        return (
            <div style={{border:"4px solid violet", backgroundColor:"pink", width:"400px", height:"400px", textAlign:"Center" }}> 
                <h1>Personal Data Form</h1>
                First Name:<input type="text" onChange={handleFirstName}/>
                Last Name:<input type="text" onChange={handleLastName}/>

            </div>
        )
    }
}

export default PersonPage
