import { Component } from "react";
import Ex3_6TasksComp from "./Ex3_6TasksComp";

class Ex3_6PersonComp extends Component
{
    constructor(props)
    {
        super(props)
    }

    render()
    {
        const tasksList = this.props.personData.tasks.map((task, index)=> {
            return <Ex3_6TasksComp taskData={task}/>
            })
        return (<div style={{border:"4px solid Green", backgroundColor:"lightcyan"}}>

                <h3>{this.props.personData.name}</h3>
                
                
                {tasksList}

        </div>)
    }
}

export default Ex3_6PersonComp;
