import React,{useEffect, useState} from 'react'
import { useDispatch } from 'react-redux'
import { Outlet, useNavigate } from 'react-router-dom'
import { moviesPermissionsPressed, subscriptionsPermissionsPressed,checkIfUsernameExist } from '../Utils/Users_Utils'


export default function AddUserComp() {
    //states:
    const [ firstName,setFirstName ] = useState('')
    const [ lastName,setLastName ]  = useState('')
    const [ username,setUsername ] = useState('')
    const [ sessionTimeOut,setSessionTimeOut ] = useState(0)
    const [ permissionsBooleans,setPermissionsBooleans ] = useState([false,false,false,false,false,false,false,false])
    const [ permissionsStrings] = useState(["View Subscriptions","Create Subscriptions","Delete Subscriptions","Update Subscriptions","View Movies","Create Movies","Delete Movies","Update Movie"])
    const [ refreshPermissions, setRefreshPermissions] = useState()

    const navigate = useNavigate()
    const dispatch = useDispatch()

    // Checking  wich permission is pressed and according to that it "checking" the "view" permissions:

    useEffect(() =>
    {
        const arr = [...permissionsBooleans]
        if(subscriptionsPermissionsPressed(permissionsBooleans))
        {
            arr[0]=true
            setPermissionsBooleans(arr)
        }
        if(moviesPermissionsPressed(permissionsBooleans))
        {
            arr[4]=true
            setPermissionsBooleans(arr)
        }
    },[refreshPermissions])

    //Saving the clients permissions input of each user:
    const changePermission = (e)=>
    {
        const newArr = [...permissionsBooleans]
        e.target.cheked ? newArr[e.target.name] = true : newArr[e.target.name] = false//if cheked in specific index (name=index) save true if not false
        setPermissionsBooleans(newArr)
        setRefreshPermissions(!refreshPermissions)
    }

    //Checking  if the username exist and if not it saves the new user in the DB.
    const saveNewUserInDB = async () => {
        let boolean = await checkIfUsernameExist(username)
        if (!bool) {
            const newArr = convertArrToPermissions(PermissionsList)
            var today = new Date()
            var date = (today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate())
            const obj = {
                firstName: FirstName,
                lastName: LastName,
                createdDate: date,
                sessionTimeOut: SessionTimeOut,
                username: UserName,
                password: '',
                permissions: newArr
            }
            Navigate('/mainPage/userManagment/allUsers')// navigating back to "allUsers" page
            dispatch({ type: "REFRESH" })
            await addUser(obj)
        } else
            alert('This Username is takken')
    }
    //List of permissions checkboxes acccordingly to useEffect's checking:
    const permissionsList = permissionsStrings.map((permission,index) =>
    <span key = {index}>
        <input type="checkbox" name={index} onChange={changePermission} checked={setPermissionsBooleans[index] ? true:false}/>{permission}<br/>
    </span>)

    return(
        <div className='addUserDiv'>
            <strong>First Name : </strong><input type="text" onChange={e => setFirstName(e.target.value)} /><br/>
            <strong>Last Name : </strong><input type="text" onChange={e => setLastName(e.target.value)} /><br/>
            <strong>User Name : </strong><input type="text" onChange={e => setUsername(e.target.value)} /><br/>
            <strong>Session Time Out (Minutes): </strong><input type="text" onChange={e => setSessionTimeOut(e.target.value)} /><br/>
            <strong>Permissions: </strong><br/>
            {permissionsList}
        </div>
    )





}
