    return (
        <div>
            <h3>{movie.name}, {year}</h3>
            Geners: {genres}<br /><br />
            <img src={img} alt={movie.name} /><br />
            {checkIfWatched ? <SubWatchedMovieComp movieId={movie._id} /> : null}
            <button onClick={editMovieButton}>Edit</button>
            <button onClick={deleteMovieButton}>Delete</button>
        </div>
    )
}
