import React, { Component } from "react";
import './Demo1_HelloWorld.css'

class HelloWorldComp extends Component
{
    constructor()
    {
        super()
    }
    
    sayHello = ()=>
    {
        alert('Hello From Your First Comp')
    }

    render()
    {

        const btnVal = "Hellllloooo"
        return (<div style={{border:'4px solid blue', backgroundColor:'lightskyblue'}}>

            <h1 className="blueStyle"> Hello World Comp</h1>

            <input type="button" value={btnVal} onClick={this.sayHello}/>
            

        </div>)
    }
}

export default HelloWorldComp;
