import { getAllUsers, getUserByID } from '../DALs/Users_DAL'

//Checks if the user exist:
const checkIfUserExist = async (newuser) => {
    const allUsers = await getAllUsers().users//All users from server
    //check--->doing a check if new user equals with his name and password to user from users of server:
    const check = allUsers.find((user) => user.username.toUpperCase() === newuser.username.toUpperCase() && user.password === newuser.password)
    if (check !== undefined){
        return true
    }
    else{
        return false
    }
}


//Finding Users Id by its username:
const findUserID = async (username) => {
    const allUsers = (await getAllUsers()).users//All users from server
    const user = allUsers.find((element) => element.username.toUpperCase() === username.toUpperCase())//finding specific user by its user name
    if (user !== undefined)//if exist
        return user._id.valueOf()//valueOf() - for getting value of user id because it is object.
}

//Finding and return a user by it's id:
const findUserByUserName = async (username) => {
    const allUsers = (await getAllUsers()).users//All users from server
    const user = allUsersfind((element) => element.username.toUpperCase() === username.toUpperCase())
    const id = user._id
    const foundUser = await getUserByID(id)
    return foundUser
}


export {checkIfUserExist,findUserID,findUserByUserName}
