import { Component } from "react";
import Ex3_6PersonComp from "./Ex3_6PersonComp";
class Ex3_6PersonsComp extends Component
{
    constructor()
    {
        super()
    
        this.state = {
            persons: [
                {
                    name: 'Avi',
                    tasks: [
                        { title: 'Task A', completed: true },
                        { title: 'Task B', completed: false },
                    ]
                },
                {
                    name: 'Dana',
                    tasks: [
                        { title: 'Task C', completed: false },
                        { title: 'Task D', completed: true },
                    ]
                },
            ]
        }
    }

    render()
    {
        const personsList = this.state.persons.map((person, index)=> {
            return <Ex3_6PersonComp personData={person} key={index}/>
            })

        return (<div style={{border:"4px solid blue", backgroundColor:"lightsteelblue"}}>

                <h2>Persons List (Ex3_6):</h2>
                {personsList}

        </div>)
    }
}

export default Ex3_6PersonsComp;
