import { Component } from "react";
import AddPersonComp from "./Ex4_2_AddPerson";

const PersonsStyle = {border:"solid purple 2px", backgroundColor:"lightgrey"}


class Ex4_2_Persons extends Component
{
    constructor()
    {
        super()
        this.state = {persons:[]}
    }

    addPerson = (person) => 
    {
        // Option 1
        // const persons = [...this.state.persons]
        // persons.push(person)

        // Option 2
        const persons = [...this.state.persons,person]
        
        this.setState({persons})
    }

    render()
    {
        const personsRepeater = this.state.persons.map((person,index)=>
        {
            const adultPreFix = person.isAdult?'an': 'not an';
           return(<li key={index}>
               {person.name} is {person.age} years old, lives in {person.city} and he\she is {adultPreFix} adult.
           </li>)})

        return(<div style={PersonsStyle}>
            <h3>Ex4.2 Persons</h3>
            <ul>{personsRepeater}</ul>
            <AddPersonComp callback={this.addPerson}/>  
        </div>)
    }
}

export default Ex4_2_Persons;
