import { Component } from "react";
import { getUserByID,getUserTasks} from "../DAL";



const todosURL = 'https://jsonplaceholder.typicode.com/todos'
const usersURL = 'https://jsonplaceholder.typicode.com/users'

export default class Users extends Component
{
    constructor(props)
    {
        super(props)
        this.state = {name:"",email:"",tasks:[],title:""}
    }

    async componentDidUpdate()
    {
        const task = await getUserTasks(this.props.userID)
        
        this.setState({title:task.data})
    }

    async componentDidMount()
    {
        const user = await getUserByID(usersURL, this.props.userID)

        this.setState({name:user.data.name, email:user.data.email})

   
    }

   



    render()
    {
       
        return (<div style={{ border: '2px solid black',background:'khaki', borderRadius: '12px', padding: '5px', width: '300px', height: '150px',margin: 'auto', textAlign: 'left'}}>

            <h4>ID: {this.props.userID}</h4>
            Name:<input type = "text" value ={this.state.name}/><br/>
            Email:<input type = "email" value ={this.state.email}/><br/>
            Task:<input type="text" value={this.state.title}/>
            <button>Other Data</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button>Update</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
            <button>Delete</button>
          
      
        </div>)
    }
}
