import { Component } from "react";

const addPersonStyle = {border:"2px solid orange", backgroundColor:"lightgoldenrodyellow", width:"300px"}


class AddPersonComp extends Component
{
    constructor()
    {
        super()
        this.state = {name:'',age:'',city:'',isAdult:false}

    }

    handleValue = (e) =>
    {
        const {name,value} = e.target
        this.setState({[name]:value})
    }

    handleCheck = (e) =>
    {
        // Option 1
        const {checked} = e.target
        this.setState({isAdult:checked})

        // Option 2
        //this.setState({isAdult:e.target.checked})
    }

    sendData = () =>
    {
        // Option 1
        const person = {...this.state}
        this.props.callback(person);

        // Option 2
        //const person = {name:this.state.name,age:this.state.age,city:this.state.city,isAdult:this.state.isAdult}
        

    }
    render()
    {
        return (<div style={addPersonStyle}>

            <h4>Add Person</h4>
            Name: <input type="text" name="name" value={this.state.name} onChange={this.handleValue}/><br/>
            Age: <input type="number" name="age" value={this.state.age} onChange={this.handleValue}/><br/>
            City: <select name="city" onChange={this.handleValue}>
                    <option value=""></option>
                    <option value="Tel Aviv">Tel Aviv</option>
                    <option value="Jerusalem">Jerusalem</option>
                    <option value="Rishon Lezion">Rishon Lezion</option>
                    <option value="Kefar Saba">Kefar Saba</option>
                    <option value="Ariel">Ariel</option>
                    <option value="Itamar">Itamar</option>
                </select><br/>
            Is Adult? <input type="checkbox" onChange={this.handleCheck}/><br/>
            <button onClick={this.sendData}>Add</button>
        </div>)
    }
}

export default AddPersonComp;
