import { Component } from "react";
import './Ex3_1_Input_Color.css'

class Ex3_1_InputColorComp extends Component
{
    constructor()
    {
        super()
        this.state = {input1:''}
    }

    handleInput = (e) =>
    {
        this.setState({input1:e.target.value})
    }

    render()
    {
        let styleInput;
        styleInput = this.state.input1.length < 5?'shortStyle':'longStyle'

        return (<div className="baseDiv">

            <h3>Ex3_1</h3>
            <input className={styleInput} type="text" value={this.state.input1} onChange={this.handleInput} /><br/>
            <br/>
        </div>)
    }


}

export default Ex3_1_InputColorComp
