import React, { Component } from 'react';
import { getAllUsers,deleteUser,updateUser } from '../DALs/DAL_USERS';
import Users from './Users';

const usersURL = 'https://jsonplaceholder.typicode.com/users'

class Main extends Component {
    constructor() {
        super()
        this.state = { 
            users:[], 
            filteredUsers:[], 
            names:[], 
            emails:[], 
            refresh:false, 
            pressedID:""}
    }
    //Geting API data and saving of it in State:
    async componentDidMount() {
        const users = await getAllUsers(usersURL)
        this.setState({ users: users.data })
        this.setState({ filteredUsers: users.data })
        this.setState({ names: users.data.name })
        this.setState({ emails: users.data.email })
    }

    handleInputChange = (e) => {
        let searchedText = e.target.value; 
        const string = searchedText.toUpperCase();
        const usersArr = this.state.users
        const newArr = usersArr.filter((user) => ((user.name.toUpperCase().includes(string)) || (user.email.toUpperCase().includes(string))));
        this.setState({ filteredUsers: newArr,refresh:true })
    }

    handleDelete = (id) => {
        const newArray = deleteUser(id, this.state.filteredUsers)
        this.setState({filteredUsers:newArray,users:newArray})
    }

    handleUpdate = (obj) =>{
        console.log(this.state.filteredUsers)
        const updatedUsers = updateUser(this.state.filteredUsers,obj)
        this.setState({filteredUsersArr:updatedUsers,users:updatedUsers})
    }


    
    refreshStop=()=>{
        this.setState({refresh:false})
    }
    pressed=(id)=>{
        this.setState({pressedID:id})
    }


    render() {


        const repUsers = this.state.filteredUsers.map((user, index) => <div key={index}><Users pressedId={this.state.pressedID} updateFunction={this.handleUpdate} deleteFunction={this.handleDelete} pressed={this.pressed} refreshStopper={this.refreshStop} refresh={this.state.refresh} user={user} key={index} id={user.id} /><br /></div>)
        return (
            <div style={{ border: '2px solid black', background: 'pink', borderRadius: '12px', padding: '5px', width: '500px', textAlign: 'center', margin: 'auto' }}>

                Search <input type="text" onChange={this.handleInputChange} />&nbsp;&nbsp;
                <button>Add</button><br /><br />
                {repUsers}

            </div>
        );
    }
}
export default Main
