const getUserTasks = async (id) => 
{

    const userTodos = await getAll(`${todosURL}?userId=${id}`)
    const dataTodos = userTodos.data

    return dataTodos;
}

const uncompletedTask = async (id) =>
{
    const userTodos = await getAll(`${todosURL}?userId=${id}`)
    const dataTodos = userTodos.data
    const result = dataTodos.find(task=>task.completed=="false")
    if (result=="")
    {
        return true
    }
    if (result=="false")
    {
        return false
    }

}

export {getUserTasks, uncompletedTask }
