import React, { useEffect, useState } from 'react'
import { getAllMembers } from '../DALs/Members_DAL';
import OneMemberComp from './OneMemberComponent';

export default function AllMembersComp() {
    const [members, setMembers] = useState()
    const [boolean, setBoolean] = useState(false)

    //loading all of members:
    useEffect(async () => {
        setMembers(await getAllMembers());
    }, [boolean])

    //listing each of members
    const membersList = members?.map((member, index) => <OneMemberComp key={index} member={member} cb={() => setBoolean(!boolean)} />)

    return (
        <div cl >
            {membersList}
        </div>
    )
}
