import { Component } from "react";
import Ex4_1_ChildComp from "./Ex4_1_Child";

export default class Ex4_1_ParentComp extends Component
{
    constructor()
    {
        super()
        this.state = {inputData:''}
    }

    render()
    {
        return (<div style={{border:"4px solid hotpink", backgroundColor:"lavender"}}>
            
            <h3>Ex4.1</h3>
            <input type="text" onChange={(e)=>this.setState({inputData:e.target.value})}/>
            <Ex4_1_ChildComp data={this.state.inputData}/>
        
        </div>)
    }

}
