import { Component } from "react";
import PersonComp from "../Ex1_4/PersonComp_Parent";
import './Demo5_Dynamic_Render.css'

class Demo5_Dynamic_Render extends Component
{
    constructor()
    {
        super()
        this.state = {isBlue:true, isVisible:true, isExist:true}
    }
    
    changeColor = () =>
    {
        // let currentColor = this.state.isBlue;
        // this.setState({isBlue:!currentColor})
        
        this.setState((prevState) => ({isBlue:!prevState.isBlue}))
    }

    changeVisibility = () =>
    {
        this.setState((prevState) => ({isVisible:!prevState.isVisible}))
    }

    changeExistence = () =>
    {
        this.setState((prevState) => ({isExist:!prevState.isExist}))
    }

    render ()
    {
        
        let colorStyle;
        let visibility;

        colorStyle = this.state.isBlue?'blueStyle':'yellowStyle'
        visibility = this.state.isVisible?'visibileStyle':'hiddenStyle'

        let obj;

        if (this.state.isExist)
        {
            obj = (<div>
                        <h3>I'm Alive!</h3>
                        <h4>Good Morning</h4>
                    </div>)
        }
        
        return (<div style={{backgroundColor:'lightblue', border:"blue 4px solid"}}>
            <h2> Dynamic Render Component</h2>
            <hr/>
            {/* 
            Option 1 - Inline
            <h3 style={{color:this.state.isBlue?'blue':'yellow'}}>Hello</h3> */}
            {/* Option 2 - css File*/}
            <h3 className={colorStyle}>Hello</h3>
            <input type="button" value="change Color" onClick={this.changeColor}/>
            <hr/>

            <h3 className={visibility}>I'm Visible!!!</h3>
            <input type="button" value="Change Visibility" onClick={this.changeVisibility}/>
            <hr/>

            {obj}
            <input type="button" value="Create/Destroy" onClick={this.changeExistence}/>
            
            <br/><br/>

        </div>)
    }
}

export default Demo5_Dynamic_Render
