import axios from 'axios';
const postsURL ='https://jsonplaceholder.typicode.com/posts'

//get all posts:
const getAll = async (url) =>await axios.get(url);

//Getting all 

const getUserPosts = async (id) => 
{
    const userPosts = await getAll(`${postsURL}?userId=${id}`)
    const dataPosts = userPosts.data  
    return dataPosts;
}

export {getUserPosts }
